use tadreebi_deplom;

drop table if exists all_tasnif_sa;
create table all_tasnif_sa
(
    est varchar(200),
    school_id  int(11), 
    pt_license varchar(30),
    city varchar(128),
    division varchar(200),
    name_pt varchar(200),
    course_program_id  bigint(20),
    pt_deplom_id  int(11), 
    code_sa varchar(64),
    name_sa varchar(200),
    level varchar(64),
    code_level varchar(64),
    levels_template_id smallint(4),
    school_level_order smallint(3),
    level_class_order smallint(3)
    
) ENGINE=MyISAM DEFAULT CHARSET=utf8;







update all_tasnif_sa sa set sa.pt_license = (select max(license) from tadreebi_org.establishment e where e.org_name = sa.est);

update all_tasnif_sa sa set sa.pt_deplom_id = (select max(ID) from deplom d where d.deplom = sa.name_pt);


