update khotta kh set kh.program_sa_code=(select code_sa from all_tasnif_sa sa where sa.pt_license = kh.license and sa.pt_deplom_id = kh.ID);

update khotta kh set kh.level_sa_code=(select code_level from all_tasnif_sa sa where sa.pt_license = kh.license and sa.pt_deplom_id = kh.ID);